import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

function Expenses(props) {
  const [choicedata, setFilteredData] = useState('2020');

  const saveDropdownDataHandler = (dropChoice) => {
    console.log(dropChoice);
    setFilteredData(dropChoice);
  //  setFilteredData((previousState) => {
  //    return (previousState, choicedata);
  //  })


  };
    return (
      <div>
        <ExpensesFilter selected={choicedata} onSaveDropdownData={saveDropdownDataHandler}/>
        <Card className="expenses">
          {props.items.map( (item) => 
              <ExpenseItem title={item.title}
                          amount={item.amount}
                          date={item.date} />)
          }
        </Card>
      </div>
    )
}

export default Expenses;
