import ExpenseItem from "./ExpenseItem"

const ExpensesList = props => {

    {props.items.length === 0 && <p>No expenses found</p>} 
    {props.items.length > 0 && //se la lista filtrata è lunga 0 = non ci sono spese quell'anno, mostra l'aviso, altrimenti mostra la lista delle spese dell'anno
    props.items.map( (expense) => 
        <ExpenseItem title={expense.title} //faccio map solo sul nuovo array restituito da filter e ritorno solo i dati filtrati
                    amount={expense.amount}
                    date={expense.date}
                    key={expense.id} />)
     }

}

export default ExpensesList;
